export function Addbolgtile({index, tile}){
    // console.log(index)
    let node = document.querySelector(".pagetop-blog-tile-image-container");
        if (node) {
            node.parentNode.removeChild(node);
        }
    return(
        <>
            <div className="blog-tile-container">
                {index===0?<Addimagetotopele value={tile.img} />:null}
                <div className="blog-tile-content-container">
                    <div className="blog-tile-profile-name-container">
                        <div className="blog-tile-profile-container">
                            <Icon icon={tile.profile===""?"emojione-v1:cat-face":tile.profile} />
                        </div>
                        <div className="blog-tile-name-date-container">
                            <div className="blog-tile-name-container">
                                <span className="blog-tile-name">
                                    {tile.name}
                                </span>
                            </div>
                            <div className="blog-tile-date-container">
                                <span className="bolg-tile-date">
                                    {tile.date}
                                </span>
                            </div>
                        </div>
                    </div>
                    <div className="blog-tile-title-container">
                        <a href="">
                            <h2>{tile.title}</h2>
                        </a>
                    </div>
                    <div className="blog-tile-tags-container">
                        <a href="">#webdev</a>
                        <a href="">#opensource</a>
                        <a href="">#javascript</a>
                        <a href="">#frontend</a>
                    </div>
                    <div className="blog-tile-comment-save-post-container">
                        <a href="">
                            <div className="blog-tile-comment-container">
                                <div className="blog-tile-comment-icon">
                                    <Icon icon="bx:message-square" hFlip={true} />
                                </div>
                                <div className="blog-tile-comment-title">
                                    <span>Comment</span>
                                </div>
                            </div>
                        </a>
                        <button type='button'>
                            <div className="blog-tile-save-post-container">
                                <Icon icon="akar-icons:ribbon" />
                            </div>
                        </button>
                    </div>
                </div>
            </div>


        </>
    )
}
export default function BlogsList(){
    let blogs = JSON.parse(localStorage.getItem("blogs"));
    let node = document.querySelector(".all-blog-outer-container");
        if (node) {
            node.parentNode.removeChild(node);
        }
    // console.log(blogs);
    return(
        <>
            <div className="all-blog-outer-container">
                <div className="blog-grid-container">
                    {blogs.map((e,index) => (
                        <Addbolgtile key={uuidv4()} index={index} tile={e} />
                    ))}
                </div>
            </div>
        </>
    )
}